import { Link } from "react-router-dom";
import { useState } from "react";
import "./AddAPet.scss";

const AddAPet = () => {
    const [roundedAge, setRoundedAge] = useState("");
    const submitForm = async (event) => {
        console.log("submitForm()");
        console.log("event: ", event);
        console.log(event.target.elements);
        event.preventDefault();

        const response = await fetch(
            `https://shannon-blog-backend.fly.dev/availablePets`,
            {
                method: "POST",
                headers: {
                    "Content-Type": "application/json",
                },
                body: JSON.stringify({
                    name: event.target.elements.name.value,
                    age: event.target.elements.age.value,
                    breed: event.target.elements.breed.value,
                    county: event.target.elements.county.value,
                    description: event.target.elements.description.value,
                }),
            }
        );
    };
    const roundAge = (event) => {
        const ageInput = event.target;
        const currentAge = ageInput.value;
        let ageRounded = Math.round(currentAge);
        if (ageRounded === 0) {
            ageRounded = "";
        }
        setRoundedAge(ageRounded);

        // const roundedAge = Math.round(event.target.value);
    };
    return (
        <div className="container-fluid" id="addAPet">
            <div id="header">
                <Link to="/">Find Your Furever Friend</Link>
            </div>
            <div className="container">
                <form onSubmit={submitForm}>
                    <div className="row">
                        <div className="col-25">
                            <label htmlFor="name">Name</label>
                        </div>
                        <div className="col-75">
                            <input
                                type="text"
                                id="name"
                                name="name"
                                // placeholder="Name.."
                            />
                        </div>
                    </div>
                    <div className="row">
                        <div className="col-25">
                            <label htmlFor="age">Age</label>
                        </div>
                        <div className="col-75">
                            <input
                                type="number"
                                min="0"
                                step="1"
                                id="age"
                                name="age"
                                onChange={roundAge}
                                value={roundedAge}
                                // placeholder="Age.."
                            />
                        </div>
                    </div>
                    <div className="row">
                        <div className="col-25">
                            <label htmlFor="breed">Breed (if known)</label>
                        </div>
                        <div className="col-75">
                            <input
                                type="text"
                                id="breed"
                                name="breed"
                                // placeholder="Breed (if known).."
                            />
                        </div>
                    </div>
                    <div className="row">
                        <div className="col-25">
                            <label htmlFor="country">County</label>
                        </div>
                        <div className="col-75">
                            <select id="county" name="county">
                                <option value=""></option>
                                <option value="onondaga">Onondoga</option>
                                <option value="cortland">Cortland</option>
                                <option value="oswego">Oswego</option>
                            </select>
                        </div>
                    </div>
                    <div className="row">
                        <div className="col-25">
                            <label htmlFor="subject">Description</label>
                        </div>
                        <div className="col-75">
                            <textarea
                                id="description"
                                name="description"
                                // placeholder="Write something.."
                                // style="height: 200px"
                            ></textarea>
                        </div>
                    </div>
                    <div className="row">
                        <button type="submit">Submit</button>
                    </div>
                </form>
            </div>

            <div>
                <p>Click on the "Choose File" button to upload a file:</p>

                {/* <form action="/action_page.php">
                    <input type="file" id="myFile" name="filename" />
                    {/* <input type="submit" /> 
                </form> */}
            </div>
        </div>
    );
};

export default AddAPet;

